const BaseModel = require('./base');

class MetaLlama extends BaseModel {
  constructor(config) {
    super(config);
    this.$inferenceConfig = {
      ...super.$inferenceConfig,
      maxTokens: 2048,
    };
  }

  static canSupport(modelString = '') {
    return modelString.indexOf('llama3') >= 0;
  }
}

module.exports = MetaLlama;
