const BaseModel = require('./base');

// Reference: https://docs.aws.amazon.com/bedrock/latest/userguide/model-parameters-anthropic-claude-messages.html
class AnthropicClaude extends BaseModel {
  constructor(config) {
    super(config);
    this.$inferenceConfig = {
      ...super.$inferenceConfig,
      maxTokens: 4096,
    }
  }

  static canSupport(modelString = '') {
    return modelString.indexOf('claude') >= 0;
  }
}

module.exports = AnthropicClaude;
