const BaseModel = require('./base');

class Nova extends BaseModel {
  constructor(config) {
    super(config);
    this.$inferenceConfig = {
      ...super.$inferenceConfig,
      maxTokens: 5120,
      // temperature: 0.60,
      // temperature: 0.2,
      // topP: 0.3,
    };
  }

  static canSupport(modelString = '') {
    return modelString.indexOf('nova') >= 0;
  }
}

module.exports = Nova;
