class SimpleGeometry {
  static timeIntersected(a, b, inclusive = false) {
    if (a[0] === b[0] && a[1] === b[1]) {
      return true;
    }

    if ((b[0] >= a[0] && b[0] < a[1]) || (b[1] > a[0] && b[1] <= a[1])) {
      return true;
    }
  
    if ((a[0] >= b[0] && a[0] < b[1]) || (a[1] > b[0] && a[1] <= b[1])) {
      return true;
    }
  
    if (inclusive) {
      if (a[0] === b[1] || a[1] === b[0]) {
        return true;
      }
    }

    return false;  
  }
}

module.exports = SimpleGeometry;
